pragma solidity ^0.5.4;

contract NiftyWallet {

    //constants
    //include master contract address, account ID, number of transactions

     address masterContractAdd = 0xb8e2b13211aF02116187F98F4662cA9Ab23136aB;

     uint userAccountID = 0;

    /*
    / Events
    */

    event Execution();
    event ExecutionFailure();
    event Deposit(address indexed sender, uint value);
    event WalletEjection(address walletAddress);

    //constant - tx number
    //every time an old tx number is called, it is incremented
    //to stop replay attacks


    //wallet controlling address and control cold address

    function returnUserAccountAddress() public returns(address) {
        MasterContract m_c_instance = MasterContract(masterContractAdd);
        return (m_c_instance.returnUserControlAddress(userAccountID));
    }

    function returnControlAccountAddress() public returns(address) {
        MasterContract m_c_instance = MasterContract(masterContractAdd);
        return (m_c_instance.returnColdControlAddress());
    }

    //only control wallet modifier
      modifier onlyControl() {
          //if the wallet ejected the control_cold will be the 0 address
          //so this modifier condition will never be met
        address control_cold = returnControlAccountAddress();
        require(msg.sender == control_cold);
        _;
      }

      struct mStruct {
        address this_add;
        bytes txData;
    }

      function returnTxMessageToSign(bytes memory txData) private view returns(bytes32) {
        mStruct memory message = mStruct(address(this), txData);
        return keccak256(abi.encodePacked(message.this_add, message.txData));
    }


   /**
   * @dev Recover signer address from a message by using their signature
   * @param hash bytes32 message, the hash is the signed message. What is recovered is the signer address.
   * @param sig bytes signature, the signature is generated using web3.eth.sign()
   */
  function recover(bytes32 hash, bytes memory sig) internal pure returns (address) {
    bytes32 r;
    bytes32 s;
    uint8 v;

    //Check the signature length
    if (sig.length != 65) {
      return (address(0));
    }

    // Divide the signature in r, s and v variables
    // ecrecover takes the signature parameters, and the only way to get them
    // currently is to use assembly.
    // solium-disable-next-line security/no-inline-assembly
    assembly {
      r := mload(add(sig, 32))
      s := mload(add(sig, 64))
      v := byte(0, mload(add(sig, 96)))
    }

    // Version of signature should be 27 or 28, but 0 and 1 are also possible versions
    if (v < 27) {
      v += 27;
    }

    // If the version is correct return the signer address
    if (v != 27 && v != 28) {
      return (address(0));
    } else {
      return ecrecover(hash, v, r, s);
    }
  }

    /**
     * Functions that get message to sign to authenticate
     */

    //payable function so contract can receive eth
    //hopefully never used
    //but we'll see

    function()
        payable
        external
    {
        if (msg.value > 0)
            emit Deposit(msg.sender, msg.value);
    }

    function callTx(bytes memory _signedData,
                     address destination,
                     uint value,
                     bytes memory data)
    public returns (bool) {
        address userSigningAddress = returnUserAccountAddress();
        bytes32 dataHash = returnTxMessageToSign(data);
        address recoveredAddress = recover(dataHash, _signedData);
        if (recoveredAddress==userSigningAddress) {
            if (external_call(destination, value, data.length, data)) {
                emit Execution();
            } else {
                emit ExecutionFailure();
            }
            return(true);
        } else {
            return(false);
        }
    }

    // call has been separated into its own function in order to take advantage
    // of the Solidity's code generator to produce a loop that copies tx.data into memory.
    function external_call(address destination, uint value, uint dataLength, bytes memory data) private returns (bool) {
        bool result;
        assembly {
            let x := mload(0x40)   // "Allocate" memory for output (0x40 is where "free memory" pointer is stored by convention)
            let d := add(data, 32) // First 32 bytes are the padded length of data, so exclude that
            result := call(
                sub(gas, 34710),   // 34710 is the value that solidity is currently emitting
                                   // It includes callGas (700) + callVeryLow (3, to pay for SUB) + callValueTransferGas (9000) +
                                   // callNewAccountGas (25000, in case the destination address does not exist and needs creating)
                destination,
                value,
                d,
                dataLength,        // Size of the input (in bytes) - this is what fixes the padding problem
                x,
                0                  // Output is ignored, therefore the output size is zero
            )
        }
        return result;
    }

    //ejection function

    /**
     * receiver functions, starting with ERC721
     * basically all copied and pasted
    * Safe ERC721 receiver
    */
    function onERC721Received(address, address, uint256, bytes memory) public returns (bytes4) {
        return this.onERC721Received.selector;
    }

    /** Safe ERC1155 receiver
    *
    */
    bytes4 constant public ERC1155_RECEIVED_SIG = 0xf23a6e61;
      bytes4 constant public ERC1155_BATCH_RECEIVED_SIG = 0xbc197c81;
      bytes4 constant public ERC1155_RECEIVED_INVALID = 0xdeadbeef;

      // Keep values from last received contract.
      bool public shouldReject;

      bytes public lastData;
      address public lastOperator;
      uint256 public lastId;
      uint256 public lastValue;

      function setShouldReject(bool _value) public {
        shouldReject = _value;
      }

      /**
      * @notice Handle the receipt of a single ERC1155 token type.
      * @dev An ERC1155-compliant smart contract MUST call this function on the token recipient contract, at the end of a `safeTransferFrom` after the balance has been updated.
      * This function MAY throw to revert and reject the transfer.
      * Return of other than the magic value MUST result in the transaction being reverted.
      * Note: The contract address is always the message sender.
      * @param _operator  The address which called the `safeTransferFrom` function
      * @param _from      The address which previously owned the token
      * @param _id        The id of the token being transferred
      * @param _value     The amount of tokens being transferred
      * @param _data      Additional data with no specified format
      * @return           `bytes4(keccak256("onERC1155Received(address,address,uint256,uint256,bytes)"))`
      */
      function onERC1155Received(address _operator, address _from, uint256 _id, uint256 _value, bytes calldata _data )
        external view returns(bytes4)
      {
        if (shouldReject == true) {
          return ERC1155_RECEIVED_INVALID; // Some random value
        } else {
          return ERC1155_RECEIVED_SIG;
        }
      }

      /**
      * @notice Handle the receipt of multiple ERC1155 token types.
      * @dev An ERC1155-compliant smart contract MUST call this function on the token recipient contract, at the end of a `safeBatchTransferFrom` after the balances have been updated.
      * This function MAY throw to revert and reject the transfer.
      * Return of other than the magic value WILL result in the transaction being reverted.
      * Note: The contract address is always the message sender.
      * @param _operator  The address which called the `safeBatchTransferFrom` function
      * @param _from      The address which previously owned the token
      * @param _ids       An array containing ids of each token being transferred
      * @param _values    An array containing amounts of each token being transferred
      * @param _data      Additional data with no specified format
      * @return           `bytes4(keccak256("onERC1155BatchReceived(address,address,uint256[],uint256[],bytes)"))`
      */
      function onERC1155BatchReceived(address _operator, address _from, uint256[] calldata _ids, uint256[] calldata _values, bytes calldata _data)
        external view returns(bytes4)
      {
        if (shouldReject == true) {
          return ERC1155_RECEIVED_INVALID; // Some random value
        } else {
          return ERC1155_BATCH_RECEIVED_SIG;
        }
      }

}

contract MasterContract {
    function returnColdControlAddress() public returns (address);
    function returnUserControlAddress(uint account_id) public returns (address);
}

